import 'package:flutter/material.dart';
import 'package:flutter_hooks/flutter_hooks.dart';
import 'package:hooks_riverpod/hooks_riverpod.dart';
import 'package:innovation_hub/app/project/model/component_model.dart';
import 'package:innovation_hub/app/project/provider/project_provider.dart';
import 'package:innovation_hub/app/project/widgets/new_component_dialog.dart';

class ProjectComponentView extends ConsumerWidget {
  const ProjectComponentView({super.key});

  @override
  Widget build(BuildContext context, WidgetRef ref) {
    final textTheme = Theme.of(context).textTheme;
    final activeProject = ref.watch(activeProjectProvider);
    final internalComp = activeProject.internalComponents;
    final externalComp = activeProject.externalComponents;

    return LayoutBuilder(builder: (context, constraints) {
      return SingleChildScrollView(
        child: Padding(
          padding: const EdgeInsets.only(top: 12.0, left: 12, right: 12),
          child: Column(
            children: [
              //Header
              Row(
                mainAxisAlignment: MainAxisAlignment.start,
                children: [
                  Text('Components', style: textTheme.headlineMedium),
                  IconButton(
                    onPressed: () async {
                      await _showAddNewComponent(context);
                    },
                    icon: const Icon(
                      Icons.add_box,
                    ),
                  ),
                  const Spacer(),
                  Expanded(
                    child: TextFormField(
                      decoration: InputDecoration(
                        hintText: 'Search component',
                        suffixIcon: IconButton(
                          icon: const Icon(Icons.search),
                          onPressed: () {
                            //TODO - add code for searching components
                          },
                        ),
                      ),
                    ),
                  ),
                ],
              ), // Header
              //Show components
              SizedBox(
                width: double.infinity, //add this to make the wrap width as much as position
                child: Wrap(
                  alignment: WrapAlignment.start,
                  runAlignment: WrapAlignment.start,
                  spacing: 12,
                  runSpacing: 12,
                  children: internalComp
                      .map(
                        (comp) => ComponentCard(component: comp),
                      )
                      .toList(),
                ),
              ),
              SizedBox(
                width: double.infinity, // add this to make the wrap width as much as position
                child: Wrap(
                  alignment: WrapAlignment.start,
                  runAlignment: WrapAlignment.start,
                  spacing: 12,
                  runSpacing: 12,
                  children: externalComp
                      .map(
                        (comp) => ComponentCard(component: comp),
                      )
                      .toList(),
                ),
              ),
            ],
          ),
        ),
      );
    });
  }

  Future<void> _showAddNewComponent(BuildContext context) async {
    await showDialog(
        context: context,
        barrierDismissible: false,
        builder: (context) {
          return WillPopScope(
            onWillPop: () async => false,
            child: const NewComponentDialog(),
          );
        });
  }
}

enum MenuItems {
  edit,
  disable,
  delete,
}

class ComponentCard extends HookWidget {
  const ComponentCard({
    Key? key,
    required this.component,
  }) : super(key: key);
  final Component component;
  @override
  Widget build(BuildContext context) {
    final selected = useState(MenuItems.edit);
    return Card(
      elevation: 4,
      child: SizedBox(
        height: 300,
        width: 200,
        child: Padding(
          padding: const EdgeInsets.all(16.0),
          child: Column(
            children: [
              Row(
                children: [
                  Expanded(
                    child: Text(
                      component.name,
                      style: Theme.of(context).textTheme.titleLarge,
                    ),
                  ),
                  PopupMenuButton<MenuItems>(
                    initialValue: selected.value,
                    onSelected: (value) {
                      selected.value = value;
                    },
                    itemBuilder: (context) {
                      return <PopupMenuEntry<MenuItems>>[
                        const PopupMenuItem(
                          value: MenuItems.edit,
                          child: Text('Edit'),
                        ),
                        const PopupMenuItem(
                          value: MenuItems.disable,
                          child: Text('Disable'),
                        ),
                        const PopupMenuItem(
                          value: MenuItems.delete,
                          child: Text('Delete'),
                        ),
                      ];
                    },
                  ),
                ],
              ),
              Row(
                children: [
                  if (component.isInternal)
                    const Text(
                      'Internal',
                      style: TextStyle(color: Colors.blue),
                    ),
                  if (!component.isInternal)
                    const Text(
                      'External',
                      style: TextStyle(color: Colors.orange),
                    ),
                  const Spacer(),
                  CircleAvatar(
                    child: Text('${component.importance}'),
                  ),
                ],
              ),
              const Divider(),
              const Spacer(),
              ElevatedButton(
                onPressed: () {
                  //TODO - add code to add attribute for component
                },
                child: const Text('Add Attribute'),
              ),
            ],
          ),
        ),
      ),
    );
  }
}
